<pre>{@code const LOCATIONS = [{
    info: "Склад",
    box: math.box(0, 0, 0, 1, 1, 1)
}]
const HUD_ID = "HUD_locations"

function main(c) {
    let player = c.getPlayer()
    let pos = player.getPosition()

    for each(var loc in LOCATIONS) {
        if (!loc.box.contains(pos)) continue
        player.setupHUD(HUD_ID)
        player.changeHUDMorph(HUD_ID, 0, mappet.createMorph('{Background:-1828716544,Label:"Локация: [e' + loc.info + '",Name:"label"}'))
        break;
    }
}
}</pre>
